mapper.select:
SELECT T.AREA_CODE, T.AREA_NAME FROM TD_M_AREA T WHERE T.VALIDFLAG = '0' AND T.AREA_LEVEL = '20'
AND T.AREA_CODE = #{relaIndustry}
ORDER BY T.ORDER_NO

SELECT T.DEPART_ID AS ID, T.DEPART_NAME AS TEXT, T.PARENT_DEPART_ID AS PID, CASE (SELECT COUNT(1) FROM TD_M_DEPART A WHERE A.PARENT_DEPART_ID = T.DEPART_ID AND A.VALIDFLAG = '0') WHEN 0 THEN 'opened' ELSE 'closed' END STATE FROM TD_M_DEPART T WHERE T.VALIDFLAG = '0'
AND T.PARENT_DEPART_ID = #{_parameter}
ORDER BY T.ORDER_NO
